mapper.select:
select * from emp


        name like concat('%',#{name},'%')


        and gender = #{gender}


        and entryDate between #{begin} and #{end}


limit #{start} , #{pageSize}

select * from emp


        name like concat('%',#{name},'%')


        and gender = #{gender}


        and entryDate between #{begin} and #{end}


order by update_time desc

select * from emp


        and id = #{id}


        and username like concat('%',#{username},'%')


        and gender = #{gender}


        and image like concat('%',#{image},'%')


        and job = #{job}


        and phone = #{phone}


        and status = #{status}


order by update_time desc
